import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import {
 Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement
} from "chart.js";

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement);

export default function App(){
 const questions=[
  "ðŸ˜Š Calm today","ðŸŽ¯ Focused","ðŸ˜Ÿ Anxious","ðŸ˜´ Slept well","ðŸš€ Motivated"
 ];

 const [scores,setScores]=useState(Array(5).fill(3));
 const [dark,setDark]=useState(false);
 const [history,setHistory]=useState([]);

 useEffect(()=>{
  const saved=JSON.parse(localStorage.getItem("moodHistory"))||[];
  setHistory(saved);
 },[]);

 useEffect(()=>{
  document.body.className=dark?"dark":"";
 },[dark]);

 const total=scores.reduce((a,b)=>a+b,0);

 const saveToday=()=>{
  const newHistory=[...history,{date:new Date().toLocaleDateString(),score:total}];
  setHistory(newHistory);
  localStorage.setItem("moodHistory",JSON.stringify(newHistory));
 };

 const data={
  labels: history.map(h=>h.date),
  datasets:[{
    label:"Mood Trend",
    data: history.map(h=>h.score),
    borderColor:"#6a5acd",
    tension:0.4
  }]
 };

 return(
  <div className="app">
   <header>
    <h1>ðŸŒˆ MindEase</h1>
    <button onClick={()=>setDark(!dark)}>ðŸŒ“</button>
   </header>

   <div className="card">
    {questions.map((q,i)=>(
     <div key={i}>
      <label>{q}</label>
      <input type="range" min="1" max="5" value={scores[i]}
       onChange={e=>{
        const s=[...scores]; s[i]=+e.target.value; setScores(s);
       }}/>
     </div>
    ))}
    <h3>Score: {total}</h3>
    <button onClick={saveToday}>Save Today</button>
   </div>

   <div className="card">
    <h2>Weekly Trend</h2>
    <Line data={data}/>
   </div>
  </div>
 );
}
